mapper.update:
update festival_detail
set fname=#{fname}, fbg=#{fbg}, faddress=#{faddress}, ftel=#{ftel}, fhomepage=#{fhomepage},
	fcontent=#{fcontent}, fimg1=#{fimg1}, fimg2=#{fimg2}, fimg3=#{fimg3}, fimg4=#{fimg4}, fimg5=#{fimg5}
where fno=#{fno}

update festival_detail
set freview_count = (select count(fno) from festival_review where fno=#{fno})
where fno=#{fno}
